import { Link } from "react-router-dom";
import { useState } from "react";
import "./register.scss";
import axios from "axios";

const Register = () => {
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
    password: "",
    name: "",
  });

  const [err, setErr] = useState(null);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

    const handleClick = async (e) => {
      e.preventDefault();
    
      try {
        await axios.post("http://localhost:8800/API_B/auth/register", inputs);
        setErr(null); // Clear error state on successful request
      } catch (err) {
        console.log(err);  // Log the full error for debugging
        setErr(err.response?.data || "An error occurred. Please try again.");
      }
    };
    

  return (
    <div className="register">
      <div className="card">
        <div className="left">
          <h1>Lama Social.</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero cum,
            alias totam numquam ipsa exercitationem dignissimos, error nam,
            consequatur.
          </p>
          <span>Do you have an account?</span>
          <Link to="/login">
            <button>Login</button>
          </Link>
        </div>
        <div className="right">
          <h1>Register</h1>
          <form>
            <input
              type="text"
              placeholder="Username"
              name="username"
              onChange={handleChange}
              required
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              onChange={handleChange}
              required
            />
            <input
              type="password"
              placeholder="Password"
              name="password"
              onChange={handleChange}
              required
            />
            <input
              type="text"
              placeholder="Name"
              name="name"
              onChange={handleChange}
              required
            />

            <button onClick={handleClick}>Register</button>
          </form>

          {/* Display error message only if there's an error */}
          {err && <p style={{ color: "red" }}>{err}</p>}
        </div>
      </div>
    </div>
  );
};

export default Register;
